mapper.delete:
UPDATE t_collections
SET is_del = 0
WHERE collection_id = #{collectionId}

mapper.update:
UPDATE t_collections
SET is_del = 1
WHERE collection_id = #{collectionId}

mapper.select:
SELECT collection_name,
       is_del
FROM t_collections
WHERE collection_name = #{collectionName}
  AND is_del = 1;
